(function() {
  this.receipt_procent_visibility = function(prov_id) {
    if (prov_id === '-1') {
      return $('.calc_div').removeClass('invisible');
    } else {
      return $('.calc_div').addClass('invisible');
    }
  };

  $(document).ready(function() {
    var chosen_params;
    chosen_params = {
      width: '99.5%',
      disable_search: 'true'
    };
    $('#receipt_user_id').chosen(chosen_params);
    $('#receipt_payment_type_id').chosen(chosen_params);
    $('#receipt_provider_id').chosen(chosen_params).on('change', function() {
      receipt_procent_visibility($('#receipt_provider_id').val());
    });
    $('#receipt_project_id').chosen(chosen_params).on('change', function() {
      var dt, prj_id;
      prj_id = $(this).val();
      if (prj_id && (prj_id > 0 || prj_id === -1)) {
        dt = $('#receipt_date').val();
        $.getJSON('/projects/' + prj_id + '.json', {
          'data': dt
        }, function(data) {
          var delim;
          $('#prj_sum').val(data.total);
          $('#cl_debt').attr('cl_debt', to_sum(data.cl_debt));
          delim = data.total / data.cl_debt;
          $('.calc_proc').each(function() {
            var _span;
            _span = $(this);
            _span.removeClass('enabled');
            if (parseInt(_span.attr('delim')) >= delim || _span.attr('delim') === '-1') {
              _span.addClass('enabled');
            }
          });
        });
      } else {
        $('#prj_sum').val(0);
      }
    });
    $('.sum_mask').mask('# ##0', {
      reverse: true,
      maxlength: false
    });
    $('.float_mask').mask("# ##0.0", {
      reverse: true,
      maxlength: false
    });
    $('.container').on('click', '.calc_proc.enabled', function() {
      var delim;
      delim = $(this).attr('delim');
      if (delim === '-1') {
        $('#receipt_sum').val($(this).attr('cl_debt'));
      } else {
        $('#receipt_sum').val(to_sum($('#prj_sum').val() / delim));
      }
    });
  });

}).call(this);
(function() {
  this.stats_query = function() {
    var p_params;
    p_params = $("#stats_page").serialize();
    $.get('statistics', p_params, null, 'script');
    return setLoc("statistics?" + p_params);
  };

  this.create_chart = function() {
    var config, data_container, headers;
    data_container = $('#leads_chart');
    if (data_container.attr('leads') === void 0) {
      return;
    }
    headers = jQuery.parseJSON(data_container.attr('headers'));
    config = {
      element: 'leads_chart',
      data: jQuery.parseJSON(data_container.attr('leads')),
      xkey: 'month',
      ykeys: headers,
      labels: headers,
      resize: true,
      behaveLikeLine: true,
      parseTime: false
    };
    switch (data_container.attr('element')) {
      case 'Area':
        return Morris.Area(config);
      case 'Bar':
        return Morris.Bar(config);
      case 'Donut':
        return Morris.Donut(config);
    }
  };

  jQuery(function() {
    var chosen_params;
    chosen_params = {
      width: '270px'
    };
    $('#page_type').chosen(chosen_params).on('change', function() {
      return stats_query();
    });
    return create_chart();
  });

}).call(this);
