(function() {
  this.get_child_wiki = function(p_id) {
    $.get('/wiki_records/' + p_id + '/wiki_records', "", null, "script");
  };

  this.update_WikiRecord = function(id) {
    $.get('/wiki_records/' + id + '/edit', "", null, "script");
  };

  jQuery(function() {
    var chosen_params;
    $('.container').on('click', 'span.folder, .wiki_name span', function() {
      var itm_id;
      itm_id = $(this).parents('tr').attr('item');
      $('#folder' + itm_id).toggleClass('opened');
      $('#sub' + itm_id).toggleClass('hidden');
      $('#files' + itm_id).toggleClass('hidden');
      if ($('#chitem_' + itm_id).html() === '') {
        get_child_wiki(itm_id);
      }
      $('#chitem_' + itm_id).toggleClass('hidden');
    });
    $('.container').on('dblclick', '.wiki_name span', function() {
      var id;
      id = $(this).closest('tr').attr('item');
      return window.location.href = '/wiki_records/' + id + '/edit';
    });
    chosen_params = {
      width: '100%'
    };
    $('#wiki_record_parent_id').chosen(chosen_params).on('change', function() {
      stats_query();
    });
  });

}).call(this);
