function to_sum(d){ 
    if (isNaN(d)) return 0;
    s = d.toString().replace(/(\d)(?=(\d\d\d)+(?!\d))/g, "$1 ");  
    return s;
}

function intFromSum(sum){
  if (sum === undefined) return 0;
  var s = sum.replace(/ /g,'')
  var i = parseInt(s)
  if (isNaN(i)) return 0;
  return i;
}

var showNotifications = function(){ 
  var time = 5000;
  $nt = $(".alert"); 
  if ($nt.hasClass('flash_success')){ time = 2000; }
  setTimeout(function() {
    $nt.removeClass("in"); 
    setTimeout("$nt.addClass('out')",1000);
  }, time);
}

function checkTime(i){
	if (i<10){i="0" + i;}
	return i;
}

function startTime(){
	var tm=new Date();
	var h=tm.getHours();
	var m=tm.getMinutes();
	var s=tm.getSeconds();
	
	m=checkTime(m);
	s=checkTime(s);
	$(".time_h").html(h+":"+m+":"+s);
	t=setTimeout('startTime()',500);
}

var delay = (function(){
  var timer = 0;
  return function(callback, ms){
    clearTimeout (timer);
    timer = setTimeout(callback, ms);
  };
})();


var show_ajax_message = function(msg, type) {
    if (!type) {type = "success"};
    $(".js-notes").html( '<div class="alert fade-in flash_'+type+'">'+msg+'</div>');    
    showNotifications();
};



